// src/components/LoginForm.js
import React, { useState } from 'react';
import axiosInstance from '../axiosConfig';
import { useNavigate } from 'react-router-dom';

const Login = ({ setToken }) => {
    const [email, setEmail] = useState('');
    //const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const API_URL = 'http://127.0.0.1:8000/api/token/'; 
    //const API_URL_UI ='http://127.0.0.1:8000/api/current-user/'

    const handleLogin = async () => {
        try {
            const response = await axios.post(API_URL, {"email": email,"password": password });
            //const response = await axios.post(API_URL, {"username": username,"password": password });
            console.log(response.data);
            // Guardar tokens en localStorage (persiste después de cerrar el navegador)
            setToken(response.data.access); // Aquí se establece el token
            localStorage.setItem('access_token', response.data.access);
            localStorage.setItem('refresh_token', response.data.refresh);

            navigate('/dashboard');
        } catch (error) {
            alert('Login failed');
            console.log(error)
        }





    };

    const handlePasswordReset = async() => {
        navigate('/recuperar'); // Ruta con parámetros ficticios

    }  

    const handleRegistro=async()  =>{
        navigate('registro')

    } 

    return (
        <div className="login-container">
            <div className="login-box">
                <h2 className="taskmaster-title">TaskMaster:</h2>
                <div className="login-icon">
                    <i className="fas fa-user-circle"></i>
                </div>
                <div className="form-group">
                    <input 
                        type="email" 
                        placeholder="Correo" 
                        onChange={(e) => setEmail(e.target.value)} 
                        className="form-control mb-3" 
                    />
                </div>
                <div className="form-group">
                    <input 
                        type="password" 
                        placeholder="Contraseña" 
                        onChange={(e) => setPassword(e.target.value)} 
                        className="form-control mb-3" 
                    />
                </div>
                <div> <button onClick={handleLogin} className="btn btn-primary btn-block">
                    Iniciar Sesión
                </button></div>

                <button onClick={handlePasswordReset} className="btn btn-link btn-forgot-password">
                    ¿Se te olvidó la contraseña?
                </button>

                <button onClick={handleRegistro} className="btn btn-link btn-forgot-password">
                    registrarse
                </button>


            </div>
        </div>
    );
};
export default LoginForm;
