import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = ({ setToken }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const API_URL = 'http://127.0.0.1:8000/tokens/'; // Cambia la URL según tu configuración
    const API_URL_UI ='http://127.0.0.1:8000/current-user/'

    const handleLogin = async () => {
        try {
            const response = await axios.post(API_URL, {"username": username,"password": password });
            console.log(response.data);
            // Guardar tokens en localStorage (persiste después de cerrar el navegador)
            setToken(response.data.access); // Aquí se establece el token
            localStorage.setItem('access_token', response.data.access);
            localStorage.setItem('refresh_token', response.data.refresh);

            navigate('/dashboard');
        } catch (error) {
            alert('Login failed');
            console.log(error)
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card shadow">
                        <div className="card-body">
                            <h2 className="card-title text-center mb-4">Iniciar Sesión</h2>
                            <div className="form-group">
                                <input 
                                    type="text" 
                                    placeholder="Usuario" 
                                    onChange={(e) => setUsername(e.target.value)} 
                                    className="form-control mb-3" 
                                />
                            </div>
                            <div className="form-group">
                                <input 
                                    type="password" 
                                    placeholder="Contraseña" 
                                    onChange={(e) => setPassword(e.target.value)} 
                                    className="form-control mb-3" 
                                />
                            </div>
                            <button onClick={handleLogin} className="btn btn-primary btn-block">
                                Iniciar Sesión
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
