import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd'; //llamado de DragDrop para tablero
import api from '../axiosConfig';
import Lista from './Lista';
import './KanbanBoard.css'; // Importar el archivo CSS

function KanbanBoard() {
  const [listas, setListas] = useState([]);
  const [tareas, setTareas] = useState([]);
  const [nuevaLista, setNuevaLista] = useState('');
  const [nuevaTarea, setNuevaTarea] = useState({ titulo: '', lista: null });
  const [editandoTarea, setEditandoTarea] = useState(null);
  const [tituloEditado, setTituloEditado] = useState('');

  useEffect(() => {
    // Llamada para obtener listas y tareas desde el backend
    api.get('/listas/').then((response) => setListas(response.data));
    api.get('/tareas/').then((response) => setTareas(response.data));
  }, []);

  const agregarLista = () => {
    if (nuevaLista.trim() === '') return;

    api.post('/listas/', { nombre: nuevaLista })
      .then((response) => {
        setListas([...listas, response.data]);
        setNuevaLista('');
      })
      .catch((error) => {
        console.error('Error al agregar la lista:', error);
      });
  };

  const eliminarLista = (id) => {
    api.delete(`/listas/${id}/`)
      .then(() => {
        setListas(listas.filter((lista) => lista.id !== id));
        setTareas(tareas.filter((tarea) => tarea.lista !== id));
      })
      .catch((error) => {
        console.error('Error al eliminar la lista:', error);
      });
  };

  const agregarTarea = (listaId) => {
    if (nuevaTarea.titulo.trim() === '' || nuevaTarea.lista === null) return;

    const tareaParaAgregar = { ...nuevaTarea, lista: listaId };
    api.post('/tareas/', tareaParaAgregar)
      .then((response) => {
        setTareas([...tareas, response.data]);
        setNuevaTarea({ titulo: '', lista: null });
      })
      .catch((error) => {
        console.error('Error al agregar la tarea:', error);
      });
  };

  const eliminarTarea = (id) => {
    api.delete(`/tareas/${id}/`)
      .then(() => {
        setTareas(tareas.filter((tarea) => tarea.id !== id));
      })
      .catch((error) => {
        console.error('Error al eliminar la tarea:', error);
      });
  };

  const editarTarea = (tarea) => {
    setEditandoTarea(tarea.id);
    setTituloEditado(tarea.titulo);
  };

  const guardarEdicionTarea = (id) => {
    api.put(`/tareas/${id}/`, { titulo: tituloEditado })
      .then((response) => {
        setTareas(tareas.map((tarea) => (tarea.id === id ? response.data : tarea)));
        setEditandoTarea(null);
        setTituloEditado('');
      })
      .catch((error) => {
        console.error('Error al editar la tarea:', error);
      });
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const { source, destination } = result;

    const updatedTareas = [...tareas];
    const [movedTarea] = updatedTareas.splice(source.index, 1);
    movedTarea.lista = parseInt(destination.droppableId, 10);
    updatedTareas.splice(destination.index, 0, movedTarea);
    setTareas(updatedTareas);

    api.put(`/tareas/${movedTarea.id}/`, movedTarea)
      .then((response) => {
        console.log('Tarea actualizada', response.data);
      })
      .catch((error) => {
        console.error('Error al actualizar la tarea:', error);
      });
  };

  return (
    <div className="kanban-board">
      <div className="nueva-lista">
        <input
          type="text"
          value={nuevaLista}
          onChange={(e) => setNuevaLista(e.target.value)}
          placeholder="Nueva lista"
        />
        <button onClick={agregarLista}>Agregar Lista</button>
      </div>
      <DragDropContext onDragEnd={onDragEnd}>
        {listas.map((lista) => (
          <Droppable key={lista.id} droppableId={String(lista.id)}>
            {(provided) => (
              <div ref={provided.innerRef} {...provided.droppableProps} className="lista">
                <div className="lista-header">
                  <h2>{lista.nombre}</h2>
                  <button onClick={() => eliminarLista(lista.id)}>Eliminar Lista</button>
                </div>
                {tareas
                  .filter((tarea) => tarea.lista === lista.id)
                  .map((tarea, index) => (
                    <Draggable key={tarea.id} draggableId={String(tarea.id)} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="tarea"
                        >
                          {editandoTarea === tarea.id ? (
                            <div>
                              <input
                                type="text"
                                value={tituloEditado}
                                onChange={(e) => setTituloEditado(e.target.value)}
                              />
                              <button onClick={() => guardarEdicionTarea(tarea.id)}>Guardar</button>
                            </div>
                          ) : (
                            <div>
                              <p>{tarea.titulo}</p>
                              <button onClick={() => editarTarea(tarea)}>Editar</button>
                              <button onClick={() => eliminarTarea(tarea.id)}>Eliminar Tarea</button>
                            </div>
                          )}
                        </div>
                      )}
                    </Draggable>
                  ))}
                {provided.placeholder}
                <div className="nueva-tarea">
                  <input
                    type="text"
                    value={nuevaTarea.titulo}
                    onChange={(e) => setNuevaTarea({ ...nuevaTarea, titulo: e.target.value })}
                    placeholder="Nueva tarea"
                  />
                  <button onClick={() => agregarTarea(lista.id)}>Agregar Tarea</button>
                </div>
              </div>
            )}
          </Droppable>
        ))}
      </DragDropContext>
    </div>
  );
}

export default KanbanBoard;
