// src/components/KanbanBoard.js
import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import api from '../axiosConfig'; // Usar Axios para hacer peticiones al backend
import Lista from './Lista'; // Corrige el nombre y la ruta del archivo



function KanbanBoard() {
  const [listas, setListas] = useState([]);
  const [tareas, setTareas] = useState([]);

  useEffect(() => {
    // Llamada para obtener listas y tareas desde el backend
    api.get('/listas').then((response) => setListas(response.data));
    api.get('/tareas').then((response) => setTareas(response.data));
  }, []);

  const onDragEnd = (result) => {
    // Lógica para manejar el movimiento de tareas entre listas
    if (!result.destination) return;

    const { source, destination } = result;

    // Actualización lógica de las tareas al moverlas entre listas
    const updatedTareas = [...tareas];
    const [movedTarea] = updatedTareas.splice(source.index, 1);
    movedTarea.lista = destination.droppableId;
    updatedTareas.splice(destination.index, 0, movedTarea);
    setTareas(updatedTareas);

    // Enviar una solicitud para actualizar la tarea en el backend
  api.put(`/tareas/${movedTarea.id}/`, movedTarea)
  .then((response) => {
    console.log('Tarea actualizada', response.data);
  })
  .catch((error) => {
    console.error('Error al actualizar la tarea:', error);
  });
};

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      {listas.map((lista) => (
        <Droppable key={lista.id} droppableId={String(lista.id)}>
          {(provided) => (
            <div ref={provided.innerRef} {...provided.droppableProps} className="lista">
              <h2>{lista.nombre}</h2>
              {tareas
                .filter((tarea) => tarea.lista === lista.id)
                .map((tarea, index) => (
                  <Draggable key={tarea.id} draggableId={String(tarea.id)} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="tarea"
                      >
                        <p>{tarea.titulo}</p>
                      </div>
                    )}
                  </Draggable>
                ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      ))}
    </DragDropContext>
  );
}

export default KanbanBoard;
